mapper.select:
SELECT id, lover, contact_date, created_at, updated_at
FROM contact_log
WHERE id = #{id}

SELECT id, lover, contact_date, created_at, updated_at
FROM contact_log
WHERE lover = #{lover}

SELECT id, lover, contact_date, created_at, updated_at
FROM contact_log
WHERE contact_date = #{contactDate}

mapper.update:
UPDATE contact_log
SET lover = #{lover} , contact_date = #{contactDate}
WHERE id = #{id}
